var imageBuffer = msg.payload;
var results = [];
var labels = context.get("labels");
var threshold = msg.threshold;
var image_width = msg.width;
var image_height = msg.height;

//Make prediction on input image
model = context.get("model")

result = await model.executeAsync(imageBuffer);

//Map result to JSON and connect label names
var bboxes = result[0];
var scores = result[1];
var label_idxs = result[2];

for(var i=0;i<bboxes.length;i++){
    if(scores[i] >= threshold){
        newResult = {
            "bbox":[
                bboxes[i][0] * image_width,
                bboxes[i][1] * image_height,
                (bboxes[i][2] - bboxes[i][0]) * image_width,
                (bboxes[i][3] - bboxes[i][1]) * image_height
            ],
            "class":labels[label_idxs[i]],
            "label":labels[label_idxs[i]],
            "score":scores[i]
        }
        results.push(newResult)
    }
}

//Calculate object width if image_width_cm is given input message
if("image_width_cm" in msg){
    var image_width_cm = msg.image_width_cm;

    for(var j=0;j<results.length;j++){
        px_in_cm = image_width_cm / msg.width
        object_size_cm = px_in_cm * results[j].bbox[2]
        results[j].length_cm = Math.round(object_size_cm)
    }
}

// Create output message
result_message = {
    "start":msg.start,
    "labels":context.get("labels"),
    "originalResult":result,
    "thresholdType":msg.thresholdType,
    "threshold": msg.threshold,
    "image_width_cm":msg.image_width_cm,
    "image_width_cm_type":msg.image_width_cm_type,
    "topic":msg.topic,
    "payload":{
        "inference":{
            "metadata":context.get("metadata"),
            "manifest":context.get("manifest"),
            "time_ms":0,
            "result":results,
            "model":env.get("inference_model_id"),
            "type":"object detection"
        },
        "image":{
            "buffer":imageBuffer,
            "width": msg.width,
            "height": msg.height,
            "type": msg.type,
            "size": (imageBuffer).length,
            "exif":{}
        }
    }
}

// Add exif information
if(msg.exif){
     result_message.payload.image.exif = msg.exif
}

return result_message;

