        node.status({fill:"green",shape:"dot",text:"Loading model..."});
        const model = new microsoftCustomvisionTfjsNode.ObjectDetectionModel();
        await model.loadModelAsync(ModelFileName);
        context.set("model", model)
        node.status({fill:"green",shape:"dot",text:"Object detection ready."});
    } catch (err) {
        node.error("Error reading model",err)
    }
