//Define threshold
var threshold = 0;

global_settings = global.get("settings") || undefined
var thresholdType = ""

if(global_settings !== undefined){
    if("threshold" in global_settings){
        threshold = global_settings["threshold"]
        thresholdType = "global";
    }
}

else if("threshold" in msg){
    threshold = msg.threshold;
    thresholdType = "msg";
    if(threshold < 0){
        threshold = 0
    }
    else if(threshold > 1){
        threshold = 1
    }
}

else{
    threshold = env.get("threshold");
    thresholdType = "env";
}


if(global_settings !== undefined){
    if("image_width_cm" in global_settings){
        image_width_cm = global_settings["image_width_cm"]
        image_width_cm_type = "global";
    }
}

else if("image_width_cm" in msg){
    image_width_cm = msg.image_width_cm;
    image_width_cm_type = "msg";
    if(image_width_cm < 0){
        image_width_cm = 0
    }
}
else{
    image_width_cm = env.get("image_width_cm");
    image_width_cm_type = "env";
}


msg.thresholdType = thresholdType;
msg.threshold = threshold;
msg.image_width_cm = image_width_cm;
msg.image_width_cm_type = image_width_cm_type;
node.status({fill:"green",shape:"dot",text:"threshold: "+threshold+" | Image width: "+image_width_cm});
return msg;

timestamp = new Date().toISOString();
var image = msg.payload;

if(Buffer.isBuffer(image)){
    node.status({fill:"green",shape:"dot",text:timestamp + " OK"});  
    return msg;
}
else{
