const imageBuffer = msg.payload;
const modelName = env.get("model_name")
const server_url = env.get("triton_server_url")

async function createImageTensor(input){
    return tf.tidy(() => {
        const tensor = tf.node.decodeJpeg(input, 3).expandDims(0);
        const shape = tensor.shape;
        return {"tensor":tensor,"shape":shape}
    });
}

const image_tensor = await createImageTensor(imageBuffer)
const image_tensor_buffer = Buffer.from(image_tensor["tensor"].dataSync());
const image_tensor_buffer_length = image_tensor_buffer.length;

image_tensor["tensor"].dispose()

let inference_header = {
      "model_name" : "test",
      "inputs" : [
        {
          "name" : "input_tensor",
          "shape" : image_tensor["shape"],
          "datatype" : "UINT8",
          "parameters" : {
              "binary_data_size":image_tensor_buffer_length
          }
        }
      ],
      "outputs" : [
        {
          "name" : "detection_scores",
          "parameters" : {
            "binary_data" : false
          }
        },
         {
          "name" : "detection_boxes",
          "parameters" : {
            "binary_data" : false
          }
        },
         {
          "name" : "detection_classes",
          "parameters" : {
            "binary_data" : false
          }
        }
      ]
    }

let inference_header_buffer = Buffer.from(JSON.stringify(inference_header))
let inference_header_length = inference_header_buffer.length


msg.method = "POST";
msg.payload = Buffer.concat([inference_header_buffer,image_tensor_buffer])
msg.url = server_url+"/v2/models/"+modelName+"/infer";
msg.headers = {
    "Content-Type":"application/octet-stream",
    "Inference-Header-Content-Length":inference_header_length,
    "Content-Length":inference_header_length+image_tensor_buffer_length
};

time_ms =  new Date().getTime() - msg.start;
node.status({fill:"green",shape:"dot",text:time_ms+" ms"});    
return msg;

