let inference_result = msg.payload;
let results = [];
const modelName = env.get("model_name")
const model = flow.get(modelName);
const labels = model.parameters.labels;
const metadata = model.parameters.metadata;

const threshold = msg.threshold;
const image_width = msg.width;
const image_height = msg.height;
const originalImage = msg.image;
let scores;
let boxes;
let names;

function chunk(arr, chunkSize) {
  if (chunkSize <= 0) throw "Invalid chunk size";
  var R = [];
  for (var i=0,len=arr.length; i<len; i+=chunkSize)
    R.push(arr.slice(i,i+chunkSize));
  return R;
}

for(let i=0;i<(inference_result.outputs).length;i++){
    //node.warn(inference_result.outputs[i])

    if(inference_result.outputs[i]["name"] == "detection_scores"){
        scores = inference_result.outputs[i].data
    }
    else if(inference_result.outputs[i]["name"] == "detection_boxes"){
        boxes = inference_result.outputs[i].data
        boxes = chunk(boxes,4)

    }
    else if(inference_result.outputs[i]["name"] == "detection_classes"){
        names = inference_result.outputs[i].data 
    }
} 

for (let i = 0; i < scores.length; i++) {
    if (scores[i] > threshold) {
        newObject = {
            "bbox":[
                    boxes[i][1] * image_width,
                    boxes[i][0] * image_height,
                    (boxes[i][3] - boxes[i][1]) * image_width,
                    (boxes[i][2] - boxes[i][0]) * image_height
            ],
            "class":labels[names[i]-1],
            "label":labels[names[i]-1],
            "score":scores[i],
            "length_cm":NaN
            }
        results.push(newObject)
    }
}

//Calculate object width if image_width_cm is given input message
 if("image_width_cm" in msg){
    const image_width_cm = msg.image_width_cm;    
    for(let j=0;j<results.length;j++){
        px_in_cm = image_width_cm / msg.width
        object_size_cm = px_in_cm * results[j].bbox[2]
        results[j].length_cm = Math.round(object_size_cm)
    }
}

// Create output message
let result_message = {
    "labels":labels,
    "thresholdType":msg.thresholdType,
    "threshold": msg.threshold,
    "image_width_cm":msg.image_width_cm,
    "image_width_cm_type":msg.image_width_cm_type,
    "topic":msg.topic,
    "payload":{
        "inference":{
            "metadata":metadata,
            "time_ms": new Date().getTime() - msg.start,
            "validated":false,
            "result":results,
            "type":"object detection"
        },
        "image":{
            "buffer":originalImage,
            "width": msg.width,
            "height": msg.height,
            "type": msg.type,
            "size": (originalImage).length,
            "exif":{}
            }
    }
}

// Add exif information
if(msg.exif){
    result_message.payload.image.exif = msg.exif
}

node.status({fill:"blue",shape:"dot",text:result_message.payload.inference.time_ms+" ms"});           

return result_message;

//Define threshold
let threshold = 0;
const global_settings = global.get("settings") || undefined
let thresholdType = ""

if(global_settings !== undefined){
    if("threshold" in global_settings){
        threshold = global_settings["threshold"]
        thresholdType = "global";
    }
}

else if("threshold" in msg){
    threshold = msg.threshold;
    thresholdType = "msg";
    if(threshold < 0){
        threshold = 0
    }
    else if(threshold > 1){
        threshold = 1
    }
}

else{
    try{
        threshold = env.get("threshold");
        thresholdType = "env";
    }
    catch(err){
        threshold = 0.5
        thresholdType = "default";
    }
}


try{
    image_width_cm_type = "env";
    image_width_cm = JSON.parse(env.get("image_width_cm"))[msg.topic];

}
catch(err){
    image_width_cm = 130
    image_width_cm_type = "default";
}

if(image_width_cm == undefined){
    image_width_cm = 130
}


if(threshold == undefined){
    threshold = 0
}

msg.thresholdType = thresholdType;
msg.threshold = threshold;
msg.image_width_cm = image_width_cm;
msg.image_width_cm_type = image_width_cm_type;
//node.status({fill:"green",shape:"dot",text:"threshold: "+threshold+" | Image width: "+image_width_cm});
return msg;

let timestamp = new Date().toISOString();
msg.start = new Date().getTime()

if(Buffer.isBuffer(msg.payload)){
    node.status({fill:"green",shape:"dot",text:timestamp + " OK"});  
    msg.image = msg.payload;
    return msg;
}
else{
    node.error("msg.payload is not an image buffer",msg)
    node.status({fill:"red",shape:"dot",text:timestamp + " msg.payload is not an image buffer"});  
    return null;
}

let modelName = env.get("model_name")
let server_url = env.get("triton_server_url")

msg.method = "GET";
msg.url = server_url+"/v2/models/"+modelName+"/config";
return msg;

let statusCode = msg.statusCode;

if(statusCode == 200){
    let model_data = msg.payload;
    let modelName = env.get("model_name")
    parsed_value = JSON.parse(model_data.parameters.metadata.string_value)
    model_data.parameters.labels = parsed_value.labels
    model_data.parameters.metadata = parsed_value.metadata
    flow.set(modelName,model_data)  
    node.status({fill:"green",shape:"dot",text:" Model configuration loaded."});   
    flow.set("ready",true)
    return [msg,null]
}
else{
    node.status({fill:"red",shape:"dot",text:msg.statusCode+":"+msg.payload.error});   
    node.error(msg.statusCode,msg.payload)
    return [null,msg]
}

return msg;

let statusCode = msg.statusCode;
let timestamp = new Date().toISOString();

if(statusCode == 200){
    node.status({fill:"green",shape:"dot",text:"Server is running."});   
    return [msg,null]
}
else{
    node.status({fill:"red",shape:"dot",text:"Server is not available"});   
    return [null,msg]
}

let statusCode = msg.statusCode;
let timestamp = new Date().toISOString();

if(statusCode == 200){
    return [msg,null]
}
else{
    flow.set("ready",false)
    return [null,msg]
}
