var threshold = 0;

global_settings = global.get("settings") || undefined
var thresholdType = ""

if(global_settings !== undefined){
    if("threshold" in global_settings){
        threshold = global_settings["threshold"]
        thresholdType = "global";
    }
}

else if("threshold" in msg){
    threshold = msg.threshold;
    thresholdType = "msg";
    if(threshold < 0){
        threshold = 0
    }
    else if(threshold > 1){
        threshold = 1
    }
}

else{
    threshold = env.get("threshold");
    thresholdType = "env";
}

result_message = {
    "threshold":threshold,
    "thresholdType":thresholdType,
    "topic":msg.topic,
    "payload":{
        "inference":{
            "time_ms":msg.inference_time,
            "result":msg.payload,
            "model":env.get("inference_model_id")
        },
        "image":{
            "buffer":msg.image,
            "width": msg.width,
            "height": msg.height,
            "type": msg.type,
            "size": (msg.image).length,
            "exif":{}
        }
    }
}

if(msg.exif){
     result_message.payload.image.exif = msg.exif
}

return result_message;

var returnResult = false;
var threshold = msg.threshold;
var thresholdType = msg.thresholdType;
var result = msg.payload.inference.result;
var bestScore = 0
var inference_time = msg.payload.inference.time_ms 

labels = env.get("labels")
msg.labels = labels;

for(var i=0;i<result.length;i++){
    if(result[i].score > threshold && labels.includes(result[i].label)){
        returnResult = true;
    }
    if(result[i].score > bestScore && labels.includes(result[i].label)){
        bestScore = result[i].score;
    }
}

if(result.length == 0){
    node.status({fill:"yellow",shape:"dot",text:thresholdType+" "+threshold+" | "+inference_time+" ms | No results."})
    return msg;
}
else{
    node.status({fill:"green",shape:"dot",text:thresholdType+" "+threshold+" | "+inference_time+" ms | "+Math.round(bestScore*100)+" %"})
    return msg;    
}

var image_width_cm = JSON.parse(env.get("image_width_cm"))[msg.topic]

var result = msg.payload;

for(var i=0;i<result.length;i++){
      //Approximate object length
    px_in_cm = image_width_cm / msg.width

    object_size_cm = px_in_cm * result[i].bbox[2]
    result[i].length_cm = Math.round(object_size_cm)
}

return msg;
